import sysfrom PyQt5 import QtWidgetsimport RSAfrom form import Ui_MainWindowclass MyWindow(QtWidgets.QMainWindow):    def __init__(self):        super(MyWindow, self).__init__()        self.ui = Ui_MainWindow()        self.ui.setupUi(self)        self.ui.sendingMessageTextEdit.textChanged.connect(self.sendMesChanged)        self.ui.generateButton.clicked.connect(self.genBtnClicked)        self.ui.sendButton.clicked.connect(self.sendButtonClicked)        self.ui.checkButton.clicked.connect(self.checkBtnClicked)    def sendMesChanged(self):        self.ui.checkButton.setEnabled(False)        self.ui.sendButton.setEnabled(False)        self.ui.answerLineEdit.clear()        self.ui.generateButton.setEnabled(True)        self.ui.answerLineEdit.setEnabled(False)    def genBtnClicked(self):        self.ui.answerLineEdit.setEnabled(False)        self.ui.checkButton.setEnabled(False)        self.ui.sendButton.setEnabled(True)        op, priv = RSA.keys(128)        self.ui.nLineEdit.setText(str(op[0]))        self.ui.eLineEdit.setText(str(op[1]))        self.ui.dLineEdit.setText(str(priv[1]))        self.ui.signLineEdit.setText(RSA.get_mod_exponentation(RSA.get_hash(self.ui.sendingMessageTextEdit.toPlainText()), priv[0], priv[1]))    def sendButtonClicked(self):        self.ui.generateButton.setEnabled(False)        self.ui.sendingMessageTextEdit.setEnabled(False)        self.ui.sendButton.setEnabled(False)        self.ui.sentMessageTextEdit.setText(self.ui.sendingMessageTextEdit.toPlainText())        self.ui.eLineEdit2.setText(self.ui.eLineEdit.text())        self.ui.nLineEdit2.setText(self.ui.nLineEdit.text())        self.ui.sign2LineEdit.setText(self.ui.signLineEdit.text())        self.ui.checkButton.setEnabled(True)    def checkBtnClicked(self):        h = RSA.get_hash(self.ui.sentMessageTextEdit.toPlainText())        self.ui.hashLineEdit.setText(h)        result = RSA.get_mod_exponentation(self.ui.sign2LineEdit.text(), int(self.ui.nLineEdit2.text()), int(self.ui.eLineEdit2.text()))        self.ui.resultLineEdit.setText(str(result))        self.ui.answerLineEdit.setEnabled(True)        if int(h, 16) == int(result, 16):            self.ui.answerLineEdit.setStyleSheet("color: rgb(85, 255, 0);")            self.ui.answerLineEdit.setText('Granted')        else:            self.ui.answerLineEdit.setStyleSheet("color: rgb(255, 0, 0);")            self.ui.answerLineEdit.setText('Denied')        self.ui.sendingMessageTextEdit.setEnabled(True)def main():    app = QtWidgets.QApplication(sys.argv)    application = MyWindow()    application.show()    sys.exit(app.exec_())if __name__ == "__main__":    main()